package com.springcloud.demo.service.impl;import com.springcloud.demo.dao.DeptDao;import com.springcloud.demo.entity.Dept;import com.springcloud.demo.service.DeptService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * \* Created with IntelliJ IDEA. * \* User: rongjichun * \* Date: 2019-04-24 19:55 * \* Description: * \ */@Servicepublic class DeptServiceImpl implements DeptService {    @Autowired    private DeptDao dao;    @Override    public boolean add(Dept dept) {        return dao.addDept(dept);    }    @Override    public Dept get(Long id) {        return dao.findById(id);    }    @Override    public List<Dept> list() {        return dao.findAll();    }}